css:
.header-container {
    display: flex;
    justify-content: space-between;
    align-items: center;
    width: 100%;
    padding-bottom: 10px;
}

.company-section {
    display: flex;
    flex-direction: column;
}

.company-logo {
    width: 180px;
    height: 60px;
}

.company-info {
    margin-top: 5px;
    font-size: 11px;
}

.po-title {
    font-size: 24px;
    font-weight: bold;
    text-align: right;
}

.po-number {
    font-size: 14px;
    font-weight: bold;
}

.po-details {
    display: flex;
    align-items: center;
    justify-content: space-between;
    width: 100%;
    margin-top: 20px;
}

.bill-to {
    width: 50%;
}

.bill-to strong {
    font-size: 16px;
}

.invoice-details {
    width: 50%;
    text-align: right;
}

.invoice-details span {
    font-weight: bold;
}

table {
    width: 100%;
    border-collapse: collapse;
    margin-top:5px;
}

th,
td {
    padding: 8px;
    /*text-align: center;*/
}

th {
    background-color: #333;
    color: white;
    /* Ensures white text for all headers */
    font-weight: bold;
}

.item-description {
    text-align: left;
    font-weight: bold;
}

.uom {
    font-size: 12px;
    color: gray;
}

.footer{
    position: fixed; 
    bottom: 0; 
    width: 100%; 
    text-align: center; 
    font-size: 15px;
}

.total-words{
    text-align:right;
}
